import React, { useState, useEffect } from "react"
import "./style.css"
import { useParams, useHistory } from "react-router-dom" // Import useHistory
import { homeData, recommended, trending } from "../../dummyData"
import Upcomming from "../upcoming/Upcomming"

const SinglePage = () => {
  const { id } = useParams()
  const [item, setItem] = useState(null)
  const history = useHistory() // Initialize useHistory

  useEffect(() => {
    let item = homeData.find((item) => item.id === parseInt(id))
    if (item) {
      setItem(item)
    }
  }, [id])

  useEffect(() => {
    let item = trending.find((item) => item.id === parseInt(id))
    if (item) {
      setItem(item)
    }
  }, [id])

  const [rec, setRec] = useState(recommended)

  const handleWatchTrailer = () => {
    history.push(`/singlepage/${id}`) // Navigate to the single page
  }

  return (
    <>
      {item ? (
        <>
          <section className='singlePage'>
            <div className='singleHeading'>
              <h1>{item.name} </h1> <span> | {item.time} | </span> <span> HD </span>
            </div>
            <div className='container'>
              <video src={item.video} controls></video>
              <div className='para'>
                <h3>Date : {item.date}</h3>
                <p>{item.desc}</p>
                <p></p>
                <p></p>
                <p></p>
              </div>
              <div className='soical'>
                <h3>Share : </h3>
                <img src='https://img.icons8.com/color/48/000000/facebook-new.png' alt='' />
                <img src='https://img.icons8.com/fluency/48/000000/twitter-circled.png' alt='' />
                <img src='https://img.icons8.com/fluency/48/000000/linkedin-circled.png' alt='' />
              </div>
            </div>
          </section>
          <Upcomming items={rec} title='Recommended Movies' />
        </>
      ) : (
        "no"
      )}
    </>
  )
}

export default SinglePage
